  methods: {
    /**
     * @description 省份鼠标划入
     * @param {Object} province 当前省份
     * @param {MouseEvent} event 鼠标划入事件
     * */
    fOnMapItemMouseover(event, province) {
      let oItemSet;
      if (Object.prototype.toString.call(this.itemColor) === "[Object Array]")
        oItemSet = this.itemColor.find((item) => {
          return item.name === province.name;
        });
      if (oItemSet) province.fill = oItemSet.activeColor;
      else province.fill = this.defaultItemActiveColor;
      if (this.showLabelOnHover) {
        this.fShowLabelOnHover({ pageX: event.pageX, pageY: event.pageY });
        this.oCurLabel = province;
      }
    },
    /**
     * @description 省份鼠标划入
     * @param {MouseEvent} event 鼠标划入事件
     * */
    fOnMapItemMousemove(event) {
      if (this.showLabelOnHover) {
        this.fHidelabel();
        setTimeout(() => {
          this.fShowLabelOnHover({ pageX: event.pageX, pageY: event.pageY });
        }, 0);
      }
    },
    /**
     * @description 显示标签
     * @param {Object} position 当前位置
     * @property {Number} position.pageX 当前位置左原始
     * @property {Number} position.pageY 当前位置上原始
     * */
    fShowLabelOnHover(position) {
      this.bShowLabel = true;
      this.$nextTick(() => {
        this.oLabelStyle = {
          position: "fixed",
          left: `${position.pageX}px`,
          top: `${position.pageY}px`,
        };
      });
    },
    /**
     * @description 隐藏标签
     * */
    fHidelabel() {
      this.bShowLabel = false;
    },
    /**
     * @description 省份鼠标划出
     * @param {Object} province 当前省份
     * @param {MouseEvent} event 鼠标划出事件
     * */
    fOnMapItemMouseout(event, province) {
      let oItemSet;
      if (Object.prototype.toString.call(this.itemColor) === "[Object Array]")
        oItemSet = this.itemColor.find((item) => {
          return item.name === province.name;
        });
      if (oItemSet) province.fill = oItemSet.color;
      else province.fill = this.defaultItemColor;
      if (this.showLabelOnHover) this.fHidelabel();
    },
    /**
     * @description 省份鼠标点击
     * @param {Object} province 当前省份
     * @param {MouseEvent} event 鼠标点击事件
     * */
    fOnMapItemClick(event, province) {
      if (this.showContentOnClick) {
        this.bShowContent = false;
        setTimeout(() => {
          this.fShowContentOnClick({ pageX: event.pageX, pageY: event.pageY });
          this.oCurCont = province;
        }, 0);
      }
    },
    /**
     * @description 省份鼠标点击显示附加内容
     * @param {Object} position 当前位置
     * @property {Number} position.pageX 当前位置左原始
     * @property {Number} position.pageY 当前位置上原始
     * */
    fShowContentOnClick(position) {
      // if (this.bShowContent) return;
      this.bShowContent = true;
      this.$nextTick(() => {
        this.oContentStyle = {
          position: "fixed",
          left: `${position.pageX}px`,
          top: `${position.pageY}px`,
        };
      });
    },
  },
  filters: {},
  watch: {
    itemColor(aNew) {
      if (Object.prototype.toString.call(aNew) === "[Object Array]") {
        this.mapRegion.forEach((province) => {
          let oProvince = undefined;
          oProvince = aNew.find((item) => {
            return province.name === item.name;
          });
          if (oProvince) province.fill = oProvince.color;
          else province.fill = this.defaultItemColor;
        });
      } else {
        this.mapRegion.map((province) => {
          province.fill = this.defaultItemColor;
        });
      }
    },
  },
};
